// Override the default time data generator
// Überschreibe den standardmäßigen Zeitangaben-Generator

Actions.generateTimeString = function (milliseconds) {
    let remaining = milliseconds;
    const times = [];

    const days = Math.floor(remaining / 60 / 60 / 24);
    if (days > 0) {
        remaining -= days * 60 * 60 * 24;
        times.push(days + (days === 1 ? " Tag" : " Tage"));
    }
    const hours = Math.floor(remaining / 60 / 60);
    if (hours > 0) {
        remaining -= hours * 60 * 60;
        times.push(hours + (hours === 1 ? " Stunde" : " Stunden"));
    }
    const minutes = Math.floor(remaining / 60);
    if (minutes > 0) {
        remaining -= minutes * 60;
        times.push(minutes + (minutes === 1 ? " Minute" : " Minuten"));
    }
    const seconds = Math.floor(remaining);
    if (seconds > 0) {
        remaining -= seconds;
        times.push(seconds + (seconds === 1 ? " Sekunde" : " Sekunden"));
    }

    let result = "";
    if (times.length === 1) {
        result = times[0];
    } else if (times.length === 2) {
        result = times[0] + " und " + times[1];
    } else if (times.length === 3) {
        result = times[0] + ", " + times[1] + " und " + times[2];
    } else if (times.length === 4) {
        result = times[0] + ", " + times[1] + ", " + times[2] + " und " + times[3];
    }
    return result;
}

// Override Transform List action
// Überschreibe Transform List-Aktion

const Actions = this.getDBM().Actions;

Actions["Transform List"] = async (cache) => {
    const data = cache.actions[cache.index];
    const list = await this.getListFromData(data.list, data.varName, cache);

    let result = [];
    const code = data.transform;
    const nullVal = data.null;
    let defaultVal;

    try {
        defaultVal = this.eval(nullVal, cache);
    } catch (e) {
        this.displayError(data, cache, e);
        defaultVal = "";
    }

    for (let i = 0; i < list.length; i++) {
        try {
            const item = list[i];
            const val = this.eval.call(
                {
                    ...Actions,
                    ...{
                        transformListIndex: i,
                        transformListItem: item,
                        transformListArray: list
                    }
                },
                "const index = this.transformListIndex; const item = this.transformListItem; const list = this.transformListArray;\n" + code,
                cache
            );

            if (val) {
                result.push(val);
            } else if (defaultVal) {
                result.push(defaultVal);
            }
        } catch (e) {
            this.displayError(data, cache, e);
            if (defaultVal) {
                result.push(defaultVal);
            }
        }
    }

    if (result) {
        const varName2 = this.evalMessage(data.varName2, cache);
        const storage2 = parseInt(data.storage, 10);
        this.storeValue(result, storage2, varName2, cache);
    }

    this.callNextAction(cache);
};

Actions["Transform List"].bind(Actions);

// Convert internal command objects to an usable DBM variable
// Konvertiere interne Befehlsobjekte zu einer nutzbaren DBM-Variable

const { $slash: slashCmds, $user: userCmds, $msge: msgCmds, $cmds: textCmds } = this.getDBM().Bot;

let commands = [];

for(let cmds of [ slashCmds, userCmds, msgCmds, textCmds ]) {
    for(let { name, description, parameters, comType: type } of Object.values(cmds)) {
        commands.push({ name, description, parameters, type  });
    }
}

commands.sort((a, b) => a.name.localeCompare(b.name) || a.type.localeCompare(b.type) || a.description?.localeCompare(b.description));

