// Create a function to resolve command types to names
// Erstelle eine Funktion, um Befehlsarten in Namen aufzulösen

const TYPES = globalVars("commandTypes");
const NAMES = globalVars("commandTypeNames");

const resolveCommandType = function(commandType) {
    if(!Object.values(TYPES).includes(commandType)) return "Manual Command";
    return NAMES[commandType];
}

