// Create a function to break texts if needed and replace the end with break characters
// Erstelle eine Funktion, um Texte falls nötig abzubrechen und das Ende mit Abbruchzeichen zu ersetzen

const breakText = function(text, maxLength, breakCharacters = "...") {
    if(text?.length > maxLength) return text.substring(0, maxLength - breakCharacters.length) + breakCharacters;

    return text;
} 

breakText;

